body {
    margin: 0;
}
.jsgame{
    max-width: 1000px;
    margin: 0 auto;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
}
.item {
    height: 100px;
    width: calc(12.5% - 20px);
    margin: 20px;
}

.flip-container {
    perspective: 1000px;
}

.open .flipper {
	transform: rotateY(180deg);
}

.flip-container{
	width: 100%;
    height: 100%;
}
.flipper {
	transition: 0.6s;
	transform-style: preserve-3d;
    width: 100%;
    height: 100%;   
	position: relative;
}
.front, .back {
	backface-visibility: hidden;
        width: 100%;
        height: 100%;
	position: absolute;
	top: 0;
	left: 0;
}
.item .front {
    background: gray;
}
.front {
	z-index: 2;
	transform: rotateY(0deg);
}

.back {
	transform: rotateY(180deg);
}

.red .back {
    background: red;
}
.blue .back {
    background: blue;
}
.green .back {
    background: green;
}
.tomato .back {
    background: tomato;
}
.yellow .back {
    background: yellow;
}
.magenta .back {
    background: magenta;
}
.indigo .back {
    background: indigo;
}
.brown .back {
    background: brown;
}
.lime .back {
    background: lime;
}
